 section ul.ctas li a {
 padding: 10px;
 text-decoration: none !important;
 color: #3d3d3d;
 transform 400ms ease 0s;
 }

 section ul.ctas li:hover {
 transform: translateY(-1px);
 box-shadow: 0 4px 16px rgb(0 0 0 / 5%);
 border: 1px solid rgb(209, 214, 255);
 }

 section ul.ctas li a div.name {
 font-weight: var(--markdown-title-weight,600);
 color: rgb(104, 117, 217);
 }


 section.get-started div.get-started-image {
 width: 100%;
 margin-left: 20px;
 }

 section.get-started .get-started-image img {
 border-radius: 6px;
 }




 @media only screen and (max-width:1029px) {

 section.two-column ul.ctas, section.three-column ul.ctas {
 grid-template-columns: repeat(1,1fr) !important;
 }

 section.get-started {
